package CORE_JAVA_WELCOM;

public class swap_var {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=12;
		
		a= a+b;
		b=a-b;
		a=a-b;
		
		System.out.println("NEW VALUE FOR a="+a);
		System.out.println("NEW VALUE FOR b="+b);
				

	}

}
